app.views.activate=Backbone.View.extend({initialize:function(){console.log("initializing activate view")},render:function(){$(this.el).html(this.template());return this},init_activate:function(e,t){var n=this;var r={};r.id=e;var i=new app.models.user;i.save(r,{success:function(e){if(e.changed.success){bootbox.dialog({title:n.language.header_activate_message,message:n.language.body_activate_message,buttons:{main:{label:n.language.label_button,className:"btn btn-info",callback:function(){$("body").removeClass("modal-open");app.routers.router.prototype.login()}}}})}else{bootbox.dialog({title:n.language.error_message,message:n.language.error_message+" : "+e.changed.error,buttons:{main:{label:n.language.label_button,className:"btn btn-danger",callback:function(){$("body").removeClass("modal-open")}}}})}console.log(e);console.log(e.changed)},error:function(e){bootbox.dialog({title:n.language.error_message,message:n.language.error_message,buttons:{main:{label:n.language.label_button,className:"btn btn-danger",callback:function(){$("body").removeClass("modal-open")}}}});console.log(e)},url:app.const.apiurl()+"user/activate/id/"+e+"/key/"+t,"private":false})},destroy_view:function(){this.undelegateEvents();$(this.el).removeData().unbind();this.remove();Backbone.View.prototype.remove.call(this);app.global.activateView=null}})